attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 lp;uniform mat4 mi;uniform bool is;uniform float el;uniform sampler2D sl;uniform sampler2D pl;uniform vec2 em;uniform float oo;uniform vec2 ss;uniform float ms;uniform vec2 pm;uniform bool ll;uniform sampler2D il;varying vec3 dd;varying vec3 gc;varying vec2 hg;varying vec4 dh; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/em.x);float f=floor($auxpos1.x/em.y)/em.y;float g=(texture2D(sl,vec2(e,f))).r;float h=(texture2D(pl,vec2(e,f))).r;float i=mod(ms,oo);float j=getExpoEaseOutValue(i,g,h,oo);if(ll) j=h;float k=getLinearValue(pm,j);if(is){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(ss,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(ss,k);}if($auxpos1.y==a) hg=vec2(0.0,0.0);else if($auxpos1.y==d) hg=vec2(0.0,1.0);else if($auxpos1.y==b) hg=vec2(1.0,0.0);else if($auxpos1.y==c) hg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=lp*mi*vec4(o,1.0);dd=normalize(p);gc=o;dh=texture2D(il,vec2(k,0.5));}else{float t=getScope(ss,k);mat4 r=getTransMat($position);float u=($auxpos1.y-el/2.0)*(20.0/el);float v=($auxpos1.z-el/2.0)*(20.0/el);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(ss,k);vec3 o=(r*vec4(t*($auxpos1.y/el-0.5),t*($auxpos1.z/el-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=lp*mi*vec4(o,1.0);dd=p;gc=o;hg=$auxpos1.yz/(el-1.0);}}
